nasal.load:
      var self = cmdarg();
      var animation_tilt = self.getNode("sim/model/v22/animation_tilt",1);
      var blade_folding = self.getNode("sim/model/v22/blade_folding",1);
      var wing_rotation = self.getNode("sim/model/v22/wing_rotation", 1);
      var blade_folding_mp = self.getNode("sim/multiplay/generic/float[0]", 1);
      var animation_tilt_mp = self.getNode("sim/multiplay/generic/float[1]", 1);
      var wing_rotation_mp = self.getNode("sim/multiplay/generic/float[2]", 1);
      var blade_incidence_mp = self.getNode("sim/multiplay/generic/float[3]", 1);
	
      var blade_incidence0 = self.getNode("rotors/main/blade[0]/incidence-deg", 1);
      var blade_incidence1 = self.getNode("rotors/main/blade[1]/incidence-deg", 1);
      var blade_incidence2 = self.getNode("rotors/main/blade[2]/incidence-deg", 1);
      var blade_incidence10 = self.getNode("rotors/tail/blade[0]/incidence-deg", 1);
      var blade_incidence11 = self.getNode("rotors/tail/blade[1]/incidence-deg", 1);
      var blade_incidence12 = self.getNode("rotors/tail/blade[2]/incidence-deg", 1);
      var loopid = 0;
      var loop = func(id) {
                 id == loopid or return;
                 var v = blade_folding_mp.getValue();
                 if ( v == nil) v = 0.0;
                 blade_folding.setDoubleValue( v );

                 v = animation_tilt_mp.getValue();
                 if ( v == nil) v = 0.0;
                 animation_tilt.setDoubleValue( v );

                 v = wing_rotation_mp.getValue();
                 if ( v == nil) v = 0.0;
                 wing_rotation.setDoubleValue( v );

                 v = blade_incidence_mp.getValue();
                 if ( v == nil) v = 0.0;
                 blade_incidence0.setDoubleValue( v );
                 blade_incidence1.setDoubleValue( v );
                 blade_incidence2.setDoubleValue( v );
                 blade_incidence10.setDoubleValue( v );
                 blade_incidence11.setDoubleValue( v );
                 blade_incidence12.setDoubleValue( v );

                 settimer(func { loop(id) }, 0);
      }
      settimer(func { loop(loopid) }, 0);
